<?php include('connect.php'); $id = $_POST['id']; $req_id = $_POST['req_id']; $qty = $_POST['qty']; $qty_received = $_POST['qty_received']; $query3 = "SELECT * FROM tbl_requests WHERE req_no='$req_id'"; $res3 = odbc_exec($connect,$query3); while($row3 = odbc_fetch_array($res3)) { $needed_qty = $row3['qty']; } if ($qty < $qty_received) { header('Location: /helpdesk/requests.php?error=1'); } else if ($needed_qty < $qty_received) { header('Location: /helpdesk/requests.php?error=2'); } else if ($needed_qty >= $qty_received && $qty >= $qty_received) { $qty_left = $qty - $qty_received; $query2 = "UPDATE tbl_requests SET inv_id ='$id', qty_received ='$qty_received' WHERE req_no ='$req_id'"; $res2 = odbc_exec($connect,$query2); $query1 = "UPDATE tbl_inventory SET qty ='$qty_left' WHERE id='$id'"; $res1 = odbc_exec($connect,$query1); if ($res1 && $res2) { header('Location: /helpdesk/requests.php'); } else { echo "FAILED!"; } } ?>